sp_widget.client_script:
api.controller = function($scope) {
    /* widget controller */
    var c = this;
    $scope.type_selected = "all";
    $scope.available_selected = "all";
    $scope.show_cards = true;
    c.data.prodotto_list = c.data.prodotti;

    $scope.showSelectedcard = function(id) {
        $scope.show_cards = false;
        $scope.iframeURL = "/x_849476_blockbust_prodotto.do?sys_id=" + id;
    }


    $scope.filterChange = function() {
        c.data.type = $scope.type_selected;
        c.data.disponibilita = $scope.available_selected;
        c.data.action = "filter_prodotti";
        c.server.update().then(function() {
            c.data.prodotto_list = c.data.prodotti;
        });
    }


    $scope.showCards = function() {
        c.data.type = $scope.type_selected;
        c.data.disponibilita = $scope.available_selected;
        c.data.action = "filter_prodotti";
        c.server.update().then(function() {
            c.data.prodotto_list = c.data.prodotti;
            $scope.show_cards = true;
        });

    }





};

