sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */

    data.prodotti = [];
    var pro = new GlideRecord("x_849476_blockbust_prodotto");
    /*pro.addQuery("operatore.userDYNAMIC90d1921e5f510100a9ad2572f2b477fe");*/
		//filters
		if(input){
			if(input.action=="filter_prodotti"){
				if(input.type!="all"){
					pro.addQuery("tipo_prodotto="+input.type);
				}
				if(input.disponibilita!="all"){
					pro.addQuery("disponibilita="+input.disponibilita);
				}
			}
		}
    pro.query();
    while (pro.next()) {
        data.prodotti.push({
            "number": pro.number.toString(),
            "tipo_prodotto": pro.tipo_prodotto.toString(),
            "tipo_prodotto_label": pro.tipo_prodotto.getDisplayValue(),
            "titolo": pro.titolo.getDisplayValue(),
						"genere": pro.genere.getDisplayValue(),
            "costo": pro.costo.getDisplayValue(),
            "disponibilita": pro.disponibilita.toString(),
            "disponibilita_label": pro.disponibilita ? "Si" : "No",
            "sys_id": pro.sys_id.toString()
        });
    }
})();
