sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */

    data.prodotti = [];
    var pro = new GlideRecord("x_849476_blockbust_prenotazione");
    pro.addQuery("cliente.userDYNAMIC90d1921e5f510100a9ad2572f2b477fe");
		//if clientscript filters
		if(input){
			if(input.action=="filter_pro"){
				if(input.type!="all"){
					pro.addQuery("tipo_prodotto="+input.type);
				}
				if(input.stato!="all"){
					pro.addQuery("stato="+input.stato);
				}
			}
		}
    pro.query();
    while (pro.next()) {
        data.prodotti.push({
            "number": pro.number.toString(),
            "tipo_prodotto": pro.tipo_prodotto.toString(),
            "tipo_prodotto_label": pro.tipo_prodotto.getDisplayValue(),
						"prodotto":pro.prodotto.getDisplayValue(),
						"genere":pro.genere.getDisplayValue(),
            "costo": pro.costo.getDisplayValue(),
            "stato": pro.stato.toString(),
            "stato_label": pro.stato.getDisplayValue(),
            "sys_id": pro.sys_id.toString()
        });
    }
})();
