sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;

	$scope.openRequirePage=function(type){
		if(type=="all")
			top.location.href="?id=all_booking_list";
		else if(type=="new")
			top.location.href="?id=add_new_booking&sys_id=1cf225c11b2d1dd0cc841f4a234bcbee";
	}

};

